package leetcode.Microsoft;

import java.util.HashMap;

public class PalindromePermutation {

	public static HashMap<Character, Integer> populateHashMap(String s) {
		HashMap<Character, Integer> hashMap = new HashMap<>();
		for(char currentChar : s.toCharArray()) {
			hashMap.put(currentChar, hashMap.getOrDefault(currentChar,0) + 1);
		}
		return hashMap;
	}

	public static boolean canPermutePalindrome(String s) {
		HashMap<Character, Integer> hashMap = populateHashMap(s);
		System.out.println(hashMap);
		int oddCount = 0;
		for (char character: hashMap.keySet()) {
			if (hashMap.get(character) % 2 == 1) {
				oddCount++;
			}
			if (oddCount > 1) {
				return false;
			}
		}
		return true;
	}

	public static void main(String[] args) {
		String ridiculous = "";
		System.out.println(canPermutePalindrome(ridiculous));
		String[] stringArray = {"code","aab","carerac"};
		for (int i = 0; i < stringArray.length; i++) {
			System.out.println(canPermutePalindrome(stringArray[i]));
		}
	}
}
